import React, { Component } from 'react';
import CanvasJSReact from '../../assets/canvasjs.react';
import dat from '../../data/data'

var CanvasJSChart = CanvasJSReact.CanvasJSChart;

class BubbleChart extends Component {
  constructor(props) {
    super(); 
  }
	render() {
		const options = {
			animationEnabled: true,
			exportEnabled: true,
			theme: "dark2", // "light1", "light2", "dark1", "dark2"
			title:{
				text: "Purchase Quantity and Purchased Date",
			fontSize: 26
			},
			axisX: {
				title: "Purchase Date",
			logarithmic: true
			},
			axisY: {
				title: "Purchase Quantity"
			},
			data: [{
				type: "bubble",
				indexLabel: "{label}",
				toolTipContent: "<b>{label}</b><br>Purchase Date: {x}mn miles<br>Avg. Surface Temp: {y} Kelvin<br>Diameter: {z} miles",
        dataPoints: [
          dat.dashBoardData.map((el) => {
              return({label: el.first_name, x: 45, y: el.amount, z: el.amount})
          })
				]
			}]
		}

		return (
		<div>
			<h1>React Bubble Chart</h1>
			<CanvasJSChart options = {options} 
				/* onRef={ref => this.chart = ref} */
			/>
			{/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
		</div>
		);
	}
}

export default BubbleChart;
